import { FunctionComponent } from "react";
import { debounce } from "lodash";
import "./SearchBar.scss";

interface SearchBarProps {
  placeholder: string;
  value: string;
  handleSearch: (...args: any) => any;
}

const SearchBar: FunctionComponent<SearchBarProps> = ({
  placeholder,
  value,
  handleSearch,
}) => (
  <div className="search-wrapper">
    <img
      alt="search-icon"
      className="search-icon"
      src="data:image/svg+xml;utf8;base64,"
    />
    {/** Elush- debounce ensures that a call to the API does not happen for each keypress, but only after a 500 ms pause */}
    <input
      className="search"
      onKeyDown={debounce(handleSearch, 500)}
      placeholder={placeholder}
      type="text"
      defaultValue={value}
    />
  </div>
);

export default SearchBar;
